<input type="text" id="<?php echo $input->id; ?>" name="<?php echo $input->name; ?>" value="<?php echo form_format( $input->format, ( ( $input->orm_object AND $input->orm_object->{$input->name} !== NULL )?$input->orm_object->{$input->name}:$input->default_value ) ); ?>"<?php foreach( $input->options as $key=>$value ){ echo ( is_numeric( $key ) )?' '.$value:' '.$key.'="'.$value.'"'; } ?> />
